#!/usr/bin/env python3

dna = ''

# replace G with c
dna_Gc = dna.replace('G','c')
# replace C with g
dna_Cg_Gc = dna_Gc.replace('C','g')
# replace A with t
dna_At_Cg_Gc = dna_Cg_Gc.replace('A','t')
# replace T with a
dna_Ta_At_Cg_Gc = dna_At_Cg_Gc.replace('T','a')
# change back to upper case
DNA_complement = dna_Ta_At_Cg_Gc.upper()
# reverse the sequence
DNA_reverse_complement = DNA_complement[::-1]

print(f"""Originial Sequence \n 5\'{dna} 3\' \n
Complement \n 3\'{DNA_complement} 5\' \n
Reverse Complement \n 5\'{DNA_reverse_complement} 3\' \n """)

