#!/usr/bin/env python3

EcoRI = 'GAATTC'

dna = ''

# find EcoRI site in dna
dna.find(EcoRI)

# replace EcoRI site with 'G^AATTC'
dna.replace(EcoRI, 'G^AATTC')

# split dna on cut sites and store results in list
EcoRI_cut = dna.replace(EcoRI, 'G^AATTC').split('^')

# iterate over each fragment and report:
# 1) start position in original seq
# 2) end position in original seq
# 3) length of fragment
fragment_it = iter(EcoRI_cut)
for fragment in fragment_it:
	print(f"""start position in original seq: {dna.find(fragment)}
end position in original seq: {dna.find(fragment)+len(fragment)}
length of fragment: {len(fragment)}\n""")

