#!/usr/bin/env python3

# create a set with a DNA sequence
dna = set('')

print(f'set of a DNA sequence: {dna}')

# determine unique characters in seq
seq = ''

unique_nt = set(seq)
print(f'unique nt: {unique_nt}')

#iterate over each unique nt and count occurences in seq
nt_count = {}
for nt in unique_nt:
	count = seq.count(nt)
	nt_count[nt] = count

print(f"nt composition: {nt_count} \nGC content: {nt_count['G'] + nt_count['C']}")
