#!/usr/bin/env python3

# create a function that calculates the GC content of a DNA sequence

def gc_content(dna):
	dna = dna.upper()
	c_count = dna.count('C')
	g_count = dna.count('G')
	dna_len = len(dna)
	gc_content = (c_count + g_count) / dna_len
	return float(gc_content)



dna = ''

print(gc_content(dna))
